import { useState } from 'react';

function useFlip() {
  const [isFlipped, setIsFlipped] = useState(false);

  function toggleFlip() {
    setIsFlipped(!isFlipped);
  }

  return [isFlipped, toggleFlip];
}

export default useFlip;
